drop table if exists ROLE CASCADE;
drop table if exists TUSER CASCADE;
create table ROLE (id integer generated by default as identity, name varchar(32), primary key (id));
create table TUSER (id integer generated by default as identity, birthday date, email varchar(32), firstName varchar(32), lastName varchar(32), login varchar(32), password varchar(32), role_id integer, primary key (id));
